this.tooltip=function(){xOffset=10;yOffset=20;$("a.tooltip").hover(function(t){this.t=this.title;this.title="";$("body").append("<p id='tooltip'>"+this.t+"</p>");$("#tooltip").css("top",t.pageY-xOffset+"px").css("left",t.pageX+yOffset+"px").fadeIn("fast")},function(){this.title=this.t;$("#tooltip").remove()});$("a.tooltip").mousemove(function(t){$("#tooltip").css("top",t.pageY-xOffset+"px").css("left",t.pageX+yOffset+"px")})};$(document).ready(function(){tooltip()});
