$(function(){$("label.uploader-btn").click(function(){$("#"+$(this).attr("for")).click();return false});$("input.image-uploader").change(function(){var a=this;var e;if(FileReader!==undefined){var t=this.files;var r=new FileReader;r.onload=function(e){var i=$("<div/>").addClass("image-preview-wrapper").append($("<img/>").attr("src",e.target.result).addClass("image-preview"));$(a).before(i)};for(i=0;i<t.length;i++){setTimeout(function(a){r.readAsDataURL(t[a])},20*i,i)}}});$("form").on("click",".image-preview",function(){var a=parseInt($(this).data("rotation")||0)+90;if(a>=360)a=0;$(this).css("transform","rotate("+a+"deg)").data("rotation",a)})});
